/**
 * Create a schema file from a given array of JSON data.
 * @author Zachary K. Watkins, <zwatkins.it@gmail.com>
 * @year 2023
 */

import fs from 'fs'
import path from 'path'
import { jsonToZod } from 'json-to-zod'
import { log } from './utils.js'
import XLSX from 'xlsx'

function getFileHeader(workbook, sheet) {
  return `/**
 * This file validates data in the "${sheet}" sheet of the
 * "${workbook.replace(process.cwd() + path.sep, '')}" workbook.
 * @date ${new Date().toISOString()}
 * @note Generated by workbook-extract.
 * @see https://github.com/zachwatkins/pdf-data-js/tree/main/packages/workbook-extract
 */
import { z } from 'zod';

`
}

function getFileFooter() {
  return `
export default schema
`
}

/**
 * Return the schema file contents for a Zod schema file that validates the given data set.
 * @param {mixed} data - The data to convert to a schema file.
 * @param {string} target - The schema file path to write.
 * @param {boolean} [v] - Verbose logging.
 * @returns {string} - The schema file contents.
 */
export function writeSchemaFile(data, target, workbook, sheet, v) {
  log(v, 'Converting data to schema file...')

  log(v, 'Done.')
  log(v, 'Writing to file...')

  fs.writeFileSync(target, getFileHeader() + jsonToZod(data) + getFileFooter())

  log(v, 'Done.')
  log(v, 'Schema file created at', target)
}

/**
 * Create schema files for a workbook.
 * @param {object} options
 * @param {string} [options.workbook]
 * @param {boolean} [options.verbose]
 * @param {object} [options.config]
 */
export function schema({ workbook, config, verbose }) {
  if (!config || !config.schema) {
    throw new Error(`Config for command "schema" not defined:`, config?.schema)
  }

  for (let i = 0; i < config.schema.length; i++) {
    const task = config.schema[i]
    const taskWorkbook = task.workbook || workbook

    if (task.skipIfExists && fs.existsSync(task.target)) {
      return
    }

    log(verbose, `Reading ${taskWorkbook}...`)

    const wb = XLSX.readFile(taskWorkbook)

    log(verbose, 'Selecting data...')

    const data = XLSX.utils.sheet_to_json(wb.Sheets[task.sheet])

    writeSchemaFile(data, task.target, taskWorkbook, task.sheet, verbose)
  }
}

export default { schema }
